 type){\n    var points = [0,0];\n    var width = length;\n    var x = 0, y;\n   while (x++ <= width) {\n     y = Math.sin(x*frequency+i)\n     points.push([(x),400+(y*amplitude/2 + amplitude/2)].join(' '))\n   }\n   points.push([length-1,0].join(' '))\n   points.push([0,0].join(' '));\n   //console.log(flowRate[type])\n    return points;\n  }\n\n\n \n  \n\n\n\n  function draw(){\n  \t/* DONOR BLOOD*/\n    var c = ++counter1/60;\n    var bloodParent = newBlood[0].parentElement\n    \n    var w = bloodParent.getAttribute('width');\n    var h = bloodParent.getAttribute('height')/30;\n    \n    for(var i in newBlood){\n\t\tvar liquid = newBlood[i];\n\t\tvar bloodType = newBlood[i].parentElement.parentElement.parentElement.parentElement.parentElement.parentElement;\n\t\tvar typea = bloodType.classList.contains('typea');\n\t\tvar typeb = bloodType.classList.contains('typeb');\n\t\tvar typeab = bloodType.classList.contains('typeab');\n\t\tvar typeo = bloodType.classList.contains('typeo');\n\t\tswitch(true) {\n\t\t\tcase typea:\n\t\t\tliquid.setAttribute('points', move(h,.03,w,c+10,\"typea\"))\n\t\t\tbreak;\n\t\tcase typeb:\n\t\t\tliquid.setAttribute('points', move(h,.03,w,c+20,\"typeb\"))\n\t\t\tbreak;\n\t\tcase typeab:\n\t\t\tliquid.setAttribute('points', move(h,.03,w,c+50,\"typeab\"))\n\t\t\tbreak;\n\t\tcase typeo:\n\t\t\tliquid.setAttribute('points', move(h,.03,w,c+70,\"typeo\"))\n\t\t\tbreak;\n\n\t\t}\n      \n       \n    }\n    \n    /* DONOR BLOOD*/\n    \n    \n    \n    if(hypeDocument.fillBags){\n    \ttranfuseBloodIn();\n    }\n    \n    if(hypeDocument.refillBags){\n    \ttranfuseBloodOut();\n    }   \n    \n    \n    if(hypeDocument.emptyRecipients){\n    \temptyRecipient();\n    }\n\t\n    gTransfusion$.requestID = window.requestAnimationFrame(draw);\n  }\n  \n  \n  \n  \n  function tranfuseBloodIn(){\n  //figure out which bags are active\n  counter2++;\n  for(var j in hypeDocument.activeBags){\n\n    if(counter2 % 2 == 0){\n      for(var i in newBlood){\n  